mapper.select:
select
id,
time,
address,
longitude,
latitude,
detail
from cy_jqjc_data
where
longitude <= #{lonMax}
and longitude >= #{lonMin}
and latitude < #{latMax}
and latitude >=#{latMin}

       select
        id,
        longitude,
        latitude,
        time
        from cy_cldwjgj_data
        where
		type=#{type}
        and longitude <= #{lonMax}
        and longitude >= #{lonMin}
        and latitude < #{latMax}
        and latitude >=#{latMin}

      select
        id,
		name,
		address,
        longitude,
        latitude,
        phone
        from cy_xfll_data
        where
        longitude <= #{lonMax}
        and longitude >= #{lonMin}
        and latitude < #{latMax}
        and latitude >=#{latMin}

      select
        id,
		name,
		address,
        longitude,
        latitude,
        phone
        from cy_pcs_data
        where
        longitude <= #{lonMax}
        and longitude >= #{lonMin}
        and latitude < #{latMax}
        and latitude >=#{latMin}
